mapper.select:
SELECT uc.comp_org ,uc.comp_section ,uc.comp_hp ,uc.comp_branch ,c.f_seq ,c.f_open_stt ,c.f_open_end ,c.comp_seq ,c.long_desc ,c.short_desc ,c.f_mainimg ,c.f_category ,cp.f_coupon_num ,cp.f_coupon_name ,cp.f_coupon_price FROM food_company_tbl c INNER JOIN user_company_tbl uc ON(uc.comp_seq = c.comp_seq) INNER JOIN (SELECT comp_seq, count(*) f_cnt FROM food_menu_tbl group by comp_seq) m ON(m.comp_seq = c.comp_seq) LEFT JOIN (SELECT comp_seq, f_coupon_num, f_coupon_name, f_coupon_price FROM food_coupon_tbl where ROWNUM=1 ) cp ON (cp.comp_seq = c.comp_seq)
AND c.f_category = #{f_category}
and c.comp_seq = #{comp_seq}

SELECT  fo.*
		,uc.comp_org comp_org
		,uc.comp_hp comp_hp
  FROM food_order_tbl fo
  LEFT JOIN user_company_tbl uc ON(fo.comp_seq = uc.comp_seq)
 WHERE f_ocode = #{f_ocode}

		and userid = #{userid}


		and f_name = #{username}

mapper.insert:
insert into food_order_tbl (
			f_ocode
			, comp_seq
			, f_status
			, f_name
			, f_hp
			, f_receive_time
			, f_message
			, f_person_num
			, userid
			, f_serial
			, f_amount
			, f_sale_price
			, f_pay_type
			, tid
			, f_pay_price
			, f_refund_price
		) values (
			#{f_ocode}
			, #{comp_seq}
			, '주문대기'
		    , #{f_name}
			, #{f_hp}
			, #{f_receive_time}
			, #{f_message}
			, #{f_person_num}
			, #{userid}
			, #{f_serial, jdbcType=VARCHAR}
			, #{f_amount}
			, #{f_sale_price, jdbcType=VARCHAR}
			, #{f_pay_type}
			, #{tid, jdbcType=VARCHAR}
			, #{f_pay_price, jdbcType=INTEGER}
			, #{f_refund_price, jdbcType=INTEGER}
		)

insert.foreach:
INTO food_order_detail_tbl (
f_odetail_code
, f_ocode
, f_code
, f_name
, f_cnt
, f_price
) VALUES (
'${f_ocode}_${index + 1}'
, #{f_ocode}
, #{item.f_code}
, #{item.f_name}
, #{item.f_cnt}
, #{item.f_price}
)

mapper.update:
UPDATE food_order_tbl SET
	 f_status = #{new_status}

	,f_refund_price = (select tmp.f_amount from food_order_tbl tmp where tmp.f_ocode = #{f_ocode})

WHERE f_ocode = #{f_ocode}
